import 'package:flutter/material.dart';import 'Screen0.dart';import 'Screen1.dart';import 'Screen2.dart';void main()=> runApp(MyApp());class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return MaterialApp(      debugShowCheckedModeBanner: false,      home: Screen0(),      routes: {        'first': (context)=> Screen1(),        'second': (context)=> Screen2(),      },    );  }}